package com.ArraysExample;

public class ObjectArray1 {
	static Byte a;

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Default value :"+a);
		a=10;
		System.out.println("Actual value :"+a);
		System.out.println("Size of byte is :"+Byte.SIZE/8);
		System.out.println("Max value of byte is :"+Byte.MAX_VALUE);
		System.out.println("Min value of byte is :"+Byte.MIN_VALUE);
		

	}

}
